    const paramCreator = ClientApiAxiosParamCreator(Configuration);
    const request = paramCreator.apiClientGet();
    request({ method: 'GET', url: '/api/Client' })
    .then(response => {
        if (response.ok) {
        return response.json();
        } else {
        throw response;
        }
    })
    .then(data => {
        // Здесь data будет содержать полученные данные
    })
    .catch(e => {
        console.log(e);
    });

    const handleGetData = () => {


        const optionsGet = {
        
            method: 'GET',
            headers: {
            'Content-Type': 'application/json',
            },
            
        };
        
    
        // fetch('https://localhost:7205/api/Client', optionsGet)
        //     .then(response => response.json())
        //     .then(json => setData(json))
        //     .catch(error => {
        //     // Обработка ошибок
        //     console.error('Error:', error);
        // });
        // async function getData() {
        //     try {
        //         const response = await fetch('https://localhost:7205/api/Client', optionsGet);
        //         const json = await response.json();
        //         setData(json);
        //     } catch (error) {
        //         console.error('Error:', error);
        //     }
        // }
        // getData();
        
    }

    useEffect(() => {
        handleGetData();
    }, []);
    
    

    const handleDeleteData = (id) => {
        
        
        const optionsDelete = {
        
            method: 'DELETE',
            headers: {
            'Content-Type': 'application/json',
            },
            
        };
        console.log(id);
        fetch('https://localhost:7205/api/Client/' + id, optionsDelete)
            .then(response => {
                if (response.ok) {
                // Успешное удаление данных
                console.log('Данные успешно удалены');
                handleGetData();
                } else {
                // Обработка ошибки, если требуется
                console.log('Ошибка при удалении данных');
                }
            })
            .catch(error => {
                // Обработка ошибки, если требуется
                console.log('Ошибка при удалении данных:', error);
            });
    };
    

    return (
        <div>
            {data.map(post => 
                <div key = {post.id}>
                    <ClientInfo post = {post} handleDeleteData={handleDeleteData}/>          
                </div>
            )}
        </div>
    );
}


export default List
