import React, { useState, useEffect } from 'react';
import { Select, MenuItem } from '@material-ui/core';
import { VeterinariansApiFactory } from '../json/api.ts';

interface Post {
    id: string;
    name: string;
}

interface IVeterinariansInfo {
    post: {
        id : string;
        name: string;
        dateOfBirth: string;
        phoneNumber: string;
        education: string;
    };
}



const SelectVeter: React.FC = () => {
    const [name, setName] = useState<string[]>([]);
    const [data, setData]: any = useState<Post[]>([]);
    const [selectedName, setSelectedName] = useState<string | undefined>();

    const functionFromApi = VeterinariansApiFactory();

    async function updateNameInApi(name: string) {
        try {
          // Отправить запрос PUT к API с выбранным именем
          await functionFromApi.apiVeterinariansPost({ name: name }); // Здесь ты должен передать правильные параметры для запроса PUT
          console.log("Имя успешно обновлено в базе данных!");
        } catch (error) {
          console.error("Произошла ошибка при обновлении имени:", error);
        }
    }
    async function getVetDataFromApi() {
        const response = await functionFromApi.apiVeterinariansGet();
        const { data } = response;
        setData(data);
        if (selectedName) {
            updateNameInApi(selectedName);
        }
    }

    useEffect(() => {
        getVetDataFromApi();
    }, []);

    return (
        <Select value={selectedName} onChange={(event) => setSelectedName(event.target.value as string)}>
            {data.map((post) => (
                <MenuItem key={post.id} value={post.id}>{post.name}</MenuItem>
            ))}
        </Select>
    );
}

export default SelectVeter;
