import dog from '../img/dogLast.jpeg'
import { useState } from 'react';
import Modal from './Modal.jsx';
import React, { useEffect } from 'react';
import { ClientApiFactory } from '../json/api.ts';
import axios from 'axios';
import { Client } from '../json/api.ts';


export default function Content() {
    const [modalActive, setModalActive] = useState(false);
    const [name, setName] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    var f = ClientApiFactory(); 
    // async function postDataFromApi() {
    //     try {
    //         const response = await f.apiClientPost()
    //         console.log(response);
    //     } catch (error) {
    //         console.log(error);
    //     }
    // }
    // useEffect(() => {
    //     postDataFromApi();
    // }, []);
    const handlePostData = (client) => {
        console.log(client);
        f.apiClientPost(client)
            .then(response => {
                if (response.status == 200) {
                    // Успешное удаление данных
                    console.log('Данные успешно удалены');
                    
                } else {
                    // Обработка ошибки, если требуется
                    console.log('Ошибка при удалении данных');
                }
            })
            .catch(error => {
                // Обработка ошибки, если требуется
                console.log('Ошибка при удалении данных:', error);
            });
        
    
    
    // Отправка запроса
    // fetch('https://localhost:7205/api/Client', options)
    //     .then(response => response.json())
    //     .then(data => {
    //     // Обработка ответа сервера
    //     console.log(data);
    //     })
    //     .catch(error => {
    //     // Обработка ошибок
    //     console.error('Error:', error);
    //     });

    // setModalActive(false);
   


    return (
        <div className='content row' >
            <div className='presentation'>
            <img  src={dog}/>
            </div>

            <div className='blockright'>
            <label className='labelmetod'> 
                <ul>
                    NEW
                </ul>
                Современные методы обследования
                </label>
                <label className='labelvetclinic'>
                <div>Круглосуточная</div>
                <div>ветеринарная</div>
                <div>клиника</div>
                </label>
                <label className='labeltext'>
                <div>Работаем со всеми видами животных.</div>
                <div>В штабе работают врачи с большим опытом работы с кошками, собаками, </div>
                <div>а также экзотическими животными. </div>
                </label>
                <div className='RecordButton' onClick={() => setModalActive(true)}>
                    <div>
                        Записаться на приём 
                    </div>
                </div>
                
                <Modal active={modalActive} setActive={setModalActive}  >
                    <div className="conteiner">
                        <div className="form">
                        <h1>Запись</h1>
                        <form className='formRecord' onClick={() => handlePostData}>
                            <input
                                type="text"
                                required
                                placeholder="Имя"
                                id="name1"
                                className="input"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                            <input
                                type="text"
                                required
                                placeholder="Номер телефона"
                                id="tel"
                                className="input"
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                            />
                            <button id="btn" type="submit">Записаться</button>
                        </form>
                    </div>
                </div>
            </Modal>
        </div>
    </div>
  )
}
}
