import React from 'react'
import {VeterinariansApiAxiosParamCreator, VeterinariansApiFactory} from '../json/api.ts';
import { useState } from 'react';
import { useEffect } from 'react';
import VeterinariansInfo from "../components/VeterinariansInfo.tsx";
import axios from "axios";
import { MdOutlineEdit } from "react-icons/md";


var functionFromApi = VeterinariansApiFactory(); 
function Veterinarians() {
    
    const [name, setName] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [dateOfBirth, setDateOfBirth] = useState("");
    const [education, setEducation] = useState("");
    const [data, setData] = useState([]);
    
    // const veterinarians = {
    //     name: name,
    //     phoneNumber: phoneNumber,
    //     dateOfBirth: dateOfBirth,
    //     education: education
    // };
    async function getVetDataFromApi() {
        const response = await functionFromApi.apiVeterinariansGet();
        const { data } = response;
        setData(data);
    }
    useEffect(() => {
        getVetDataFromApi();
    }, []);

    const handleDeleteVetData = (id) => {
        console.log(id);
        functionFromApi.apiVeterinariansIdDelete(id)
            .then(response => {
                if (response.status == 200) {
                    // Успешное удаление данных
                    console.log('Данные успешно удалены');
                    getVetDataFromApi();
                } else {
                    // Обработка ошибки, если требуется
                    console.log('Ошибка при удалении данных');
                }
            })
            .catch(error => {
                // Обработка ошибки, если требуется
                console.log('Ошибка при удалении данных:', error);
            });
    }

    // const handlePutVetData = async (id) => {
    //     console.log(id);
    //     try {
    //         const response = await functionFromApi.apiVeterinariansIdPut(id);
    //         if (response.status === 200) {
    //           const data = await response.json();
    //             setName(data.name);
    //             setPhoneNumber(data.phoneNumber);
    //             setDateOfBirth(data.dateOfBirth);
    //             setEducation(data.education);
    //         }
    //         } catch (error) {
    //             console.log(error);
    //     }
    // }

    const handlePutVetData = async (id, data) => {
        console.log(id);
        try {
            const response = await functionFromApi.apiVeterinariansIdPut(id, data );
            if (response.status === 200) {
              const data = await response.json();
                setName(data.name);
                setPhoneNumber(data.phoneNumber);
                setDateOfBirth(data.dateOfBirth);
                setEducation(data.education);
            }
            } catch (error) {
                console.log(error);
        }
    }
        

    // const handlePutVetData =  (id) => {
    //     console.log(id);
    //     functionFromApi.apiVeterinariansIdPut(id)
    //         .then(response => {
    //             if (response.status === 200) {
    //                 const data =  response.json();
    //                 setName(data.name);
    //                 setPhoneNumber(data.phoneNumber);
    //                 setDateOfBirth(data.dateOfBirth);
    //                 setEducation(data.education);
    //                 console.log('Данные успешно удалены');
                    
    //             } else {
    //                 // Обработка ошибки, если требуется
    //                 console.log('Ошибка при удалении данных');
    //             }
    //         })
    //         .catch(error => {
    //             // Обработка ошибки, если требуется
    //             console.log('Ошибка при удалении данных:', error);
    //         });
    // };
    

    return (
        <div>
            Список ветеринаров:
            <div>
                {data.map(post => 
                    <div key = {post.id}>
                        <VeterinariansInfo post = {post} handleDeleteVetData={handleDeleteVetData}  handlePutVetData = {handlePutVetData}/>    
                        <>
                            <form onSubmit={handlePutVetData}>
                            <label>Name</label>
                            <input type='text' value={name} onChange={(e) => setName(e.target.value)} />

                                <label>Phone Number</label>
                                <input type="tel" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />

                                <label>Date of Birth</label>
                                <input type="date" value={dateOfBirth} onChange={(e) => setDateOfBirth(e.target.value)} />

                                <label>Education</label>
                                <input type="text" value={education} onChange={(e) => setEducation(e.target.value)} />

                                <button type="submit">Submit</button>
                            </form>
                            </>
                    </div>
                    
                )}
                
            </div>
        </div>
    );
}

export default Veterinarians
